# -*- coding: utf-8 -*-

from .exceptions import exception_create

def intcheck(exception,text,which=0):
    def check(function):
        def ch(*args):
            if type(args[which]) == list:
                for i in args[which]:
                    if type(i) != int:
                        raise exception_create(exception)(text)
                return function(*args)
            else:
                if type(i) != int:
                    raise exception_create(exception)(text)
                return function(*args)
        return ch
    return check

